#
# Card Id: FindTheRaft
# Card Text: Allow travel from this location. The players cannot attack this objective while Hill Troll is in play.
# Author: Chen Meiyi
class FindTheRaft:

  # Default initialization for the script
  def __init__(self):
    pass

  def whenPlayed(self, thisCard):
    enemies = _encounters.GetEnemies()
    for enemy in enemies:
      if enemy.CardId == "HillTroll":
          thisCard.AddAttribute(StagingCardAttribute.CannotBeAttacked)

  def afterCardPlayed(self, thisCard, playedCard):
    if playedCard.CardId == "HillTroll":
        thisCard.AddAttribute(StagingCardAttribute.CannotBeAttacked)

  def afterEnemyIsDefeated(self, thisCard, enemy):
    enemies = _encounters.GetEnemies()
    objectives = _encounters.GetObjectives()
    hasInfluence = False
    for objective in objectives:
      if objective.CardId == "BlockedPath" and objective.ConfigData.QuestPoints > 0:
        hasInfluence = True

    for card in enemies:
      if card.CardId == "HillTroll" and card.CurrentHealth > 0:
        hasInfluence = True

    if hasInfluence == False:
        thisCard.RemoveAttribute(StagingCardAttribute.CannotBeAttacked)

  # Triggers when this card is defeated
  def whenLeavesPlay(self, thisCard):
    AllowTravel()

