#
# Card Id: AmbushOnTheShore
# Card Text: Players cannot attack this objective while an enemy is in play. Upkeep: Sauron gains 1 resource per player. Defeat this objective to win the game.
# Author: Chen Meiyi


class AmbushOnTheShore:

    # Default initialization for the script
    def __init__(self):
        pass

    def whenPlayed(self, thisCard):
        enemies = _encounters.GetEnemies()
        if len(enemies) > 0:
            thisCard.AddAttribute(StagingCardAttribute.CannotBeAttacked)

    def afterCardPlayed(self, thisCard, playedCard):
        enemies = _encounters.GetEnemies()
        if len(enemies) > 0:
            thisCard.AddAttribute(StagingCardAttribute.CannotBeAttacked)

    def afterEnemyIsDefeated(self, thisCard, enemy):
        enemies = _encounters.GetEnemies()
        if (len(enemies) == 1 and enemies[0].CardId == enemy.CardId) or len(enemies) == 0:
            thisCard.RemoveAttribute(StagingCardAttribute.CannotBeAttacked)
        else:
            thisCard.AddAttribute(StagingCardAttribute.CannotBeAttacked)

    def afterObjectiveIsDefeated(self, thisCard, enemy):
        enemies = _encounters.GetEnemies()
        if (len(enemies) == 1 and enemies[0].CardId == enemy.CardId) or len(enemies) == 0:
            thisCard.RemoveAttribute(StagingCardAttribute.CannotBeAttacked)
        else:
            thisCard.AddAttribute(StagingCardAttribute.CannotBeAttacked)

    def atUpkeep(self, thisCard):
        ModifySauronResources(1)

    # Triggers when this card is defeated
    def whenLeavesPlay(self, thisCard):
        WinGame()

